function scrollToTop(){window.scrollTo({top:0,behavior:"smooth"})}window.addEventListener("load",()=>{document.querySelector("#back-to-top").addEventListener("click",scrollToTop)}),window.addEventListener("scroll",()=>{let e=document.documentElement.scrollTop||document.body.scrollTop;e>100?document.querySelector("#back-to-top").className="float-button show":document.querySelector("#back-to-top").className="float-button"})